package eu.falcon.semantic.security;

import com.nimbusds.jose.JOSEException;
import com.nimbusds.jose.jwk.RSAKey;
import eu.falcon.semantic.util.Util;
import java.io.IOException;
import java.security.interfaces.RSAPrivateKey;
import java.security.interfaces.RSAPublicKey;
import java.util.logging.Level;
import java.util.logging.Logger;

public enum DefaultRSAKeypairGenerator {

    INSTANCE;

    final String rsaKeyBase64 = "";

    RSAKey keypair;

    private DefaultRSAKeypairGenerator() {
        try {
            keypair = (RSAKey) Util.deserializeFromString(rsaKeyBase64);

        } catch (IOException | ClassNotFoundException ex) {
            Logger.getLogger(DefaultRSAKeypairGenerator.class.getName()).log(Level.SEVERE, null, ex);
        }
    }

    public RSAPrivateKey getPrivateKey() throws JOSEException {
        return keypair.toRSAPrivateKey();
    }

    public RSAPublicKey getPublicKey() throws JOSEException {
        return keypair.toRSAPublicKey();
    }

}
